mapper.update:
update tbl_member
set  member_email= #{memberEmail}, member_password=#{memberPassword}, member_phone_number=#{memberPhoneNumber}, member_name=#{memberName}, member_status=#{memberStatus}, member_img_original_name=#{memberImgOriginalName}, member_img_uuid=#{memberImgUuid}, member_img_path=#{memberImgPath}
where member_id = #{memberId}

update tbl_business
set business_email= #{businessEmail}, business_password= #{businessPassword}, business_ceo_name= #{businessCeoName}, business_number= #{businessNumber}, business_company_name= #{businessCompanyName}, business_phone_number= #{businessPhoneNumber}, business_establishment_date= #{BusinessEstablishmentDate}, business_status= #{businessStatus}, business_img_original_name= #{businessImgOriginalName}, business_img_uuid= #{businessImgUuid}, business_img_path= #{businessImgPath}
where business_id = #{businessId}
